<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">-&gt;</span><span style="color:#82AAFF;">stubPath</span><span style="color:#89DDFF;">(</span><span style="color:#82AAFF;">base_path</span><span style="color:#89DDFF;">(</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">stubs</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">))</span></span>
